import React from 'react';
import {ArrowDiv, Container, TextDiv, TitleTex, NormalText, ExtrasLogos, LogoDiv,Button} from "./styles"
import {FaCaretRight} from 'react-icons/fa';
import saecomp from "../../assets/img/saecomp.png";
import ada from "../../assets/img/ada.png";
import senc from "../../assets/svg/senc.svg";

interface Props{
    title:string;
    text:string;
  }

const Extras:React.FC<Props> = ({title, text}) =>{
    let ref;
    const refAssignCallback = (ref) => {
        if (ref) {
          //the containerRef is currently null, ref available = mounted.
          var element = ref;
          var scrollWidth = element.scrollWidth;
          var clientWidth = element.getBoundingClientRect().width;

          //explicitly set the scrollTop position of the scrollContainer
          element.scrollLeft = (scrollWidth - clientWidth) / 2;
        } 
      }; 

    return(
        <Container>
            <TextDiv>
                <TitleTex>
                    {title}
                </TitleTex>
                <NormalText>
                    {text}
                </NormalText>
                <ExtrasLogos>
                    <LogoDiv href="#">
                        <img src={ada} alt="saecomp" className='Ada'/>
                    </LogoDiv>
                    <LogoDiv className="centered" href="#">
                        <img src={saecomp} alt="saecomp"/>
                    </LogoDiv>
                    <LogoDiv href="#">
                        <img src={senc} alt="saecomp"/>
                    </LogoDiv>
                    <LogoDiv href="#">
                        <img src={senc} alt="saecomp"/>
                    </LogoDiv>
                </ExtrasLogos>

                <Button>E MUITAS OUTRAS</Button>
            </TextDiv>
        </Container>
    )
}
export default Extras;
