import React from 'react';
import styled from 'styled-components';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faUser } from '@fortawesome/free-solid-svg-icons'; // Ícone de usuário
import { userI } from './';

const Ul = styled.ul<{open: boolean}>`
  z-index: 100;
  list-style: none;
  display: flex;
  flex-flow: row nowrap;
  li {
    padding: 18px 3vw;
  }
  @media (max-width: 768px) {
    flex-flow: column nowrap;
    background-color: #03194C;
    position: fixed;
    transform: ${({ open }) => open ? 'translateX(0)' : 'translateX(100%)'};
    top: 0;
    right: 0;
    height: 100vh;
    width: 300px;
    padding-top: 3.5rem;
    transition: transform 0.3s ease-in-out;
    li {
      color: #fff;
    }
  }
`;

interface RightNavProps {
    open: boolean;
    isAuthenticated: boolean;
    user: userI;
    handleSignout: () => void;
}

const RightNav = ({ open, isAuthenticated, user, handleSignout }: RightNavProps) => {
  return (
    <Ul open={open}>
      <li><a href='/'>Home</a></li>
      <li><a href='/SAEComp'>SAEComp</a></li>
      <li><a href='/Enfases'>Ênfases</a></li>
      <li><a href='/Notas'>Notas</a></li>
      <li><a href='/Manual-bixo'>Manual</a></li>
      <li>
        {isAuthenticated ? (
          <div>
            {user.picture && <img src={user.picture} alt="User" style={{ width: '24px', height: '24px', borderRadius: '50%' }} />}
            <button onClick={handleSignout}>Sign out</button>
          </div>
        ) : (
          <a href='/Login'>
            <FontAwesomeIcon icon={faUser} /> Login
          </a>
        )}
      </li>
    </Ul>
  )
}

export default RightNav;
