import React, { useState } from 'react';
import styled from 'styled-components';
import NavBar from "../../components/NavBar";
import Footer from "../../components/Footer";
import GoogleButton from '../../components/GoogleButton';
import { Container } from "./styles";
import { Navbar } from 'react-bootstrap';
import jwtDecode from "jwt-decode";

const LeftContainer = styled.div`
  flex: 1;
  display: flex;
  justify-content: center;
  align-items: center;
  background-color: #f9f9f9;
`;

const FormContainer = styled.div`
  width: 300px;
  text-align: center;
`;

const Title = styled.h2`
  margin-bottom: 24px;
  font-size: 24px;
  color: #333;
`;

const Input = styled.input`
  width: 100%;
  padding: 12px;
  margin-bottom: 16px;
  border-radius: 8px;
  border: 1px solid #ccc;
`;

const Button = styled.button`
  width: 100%;
  padding: 12px;
  border-radius: 8px;
  border: none;
  background-color: #4f46e5;
  color: white;
  font-size: 16px;
  cursor: pointer;
  margin-bottom: 12px;

  &:hover {
    background-color: #4338ca;
  }
`;

const RememberMeContainer = styled.div`
  display: flex;
  align-items: center;
  justify-content: space-between;
  margin-bottom: 16px;
`;

const RememberMe = styled.label`
  display: flex;
  align-items: center;

  input {
    margin-right: 8px;
  }
`;

const RightContainer = styled.div`
  flex: 1;
  background-color: #4f46e5;
  display: flex;
  align-items: center;
  justify-content: center;
  padding: 24px;
`;

const RightBackground = styled.div`
  width: 100%;
  height: 100%;
  background-image: url('https://source.unsplash.com/random');
  background-size: cover;
  background-position: center;
  border-radius: 12px;
`;

export interface userI {
    name: string | null;
    iat?: number;
    iss?: string;
    picture?: string;
}

const Login: React.FC = () => {
    const [user, setUser] = useState<userI>({ name: null });

    function handleLoginSuccess(userObject: any) {
        setUser(userObject as userI);
        localStorage.setItem("token", JSON.stringify(userObject));
    }

    function handleCallbackResponse(response: any) {
        console.log("Encoded JWT ID token:" + response.credential);
        const userObject = jwtDecode(response.credential);
        console.log(userObject)

        setUser(userObject as userI);
        document.getElementById("signInDiv")!.hidden = true;
        localStorage.setItem("token", "userObject");
    }
    function handleSignout() {
        setUser({ name: null });
        document.getElementById("signInDiv")!.hidden = false;
        localStorage.removeItem("token");
    }

    return (
        <Container>
            <LeftContainer>
                <FormContainer>
                    <Title>Bem vindos à Eng Comp!</Title>
                    <Input type="email" placeholder="Enter your email address" />
                    <Input type="password" placeholder="Enter password" />
                    <RememberMeContainer>
                        <RememberMe>
                            <input type="checkbox" />
                            <span>Remember me</span>
                        </RememberMe>
                        <a href="/">Forgot your password?</a>
                    </RememberMeContainer>
                    <Button>Log in</Button>

                    {/* Substituindo o botão Google pelo componente funcional GoogleButton */}
                    <GoogleButton onLoginSuccess={handleLoginSuccess} />

                    <p>
                        Don't have an account? <a href="/">Register here</a>
                    </p>
                </FormContainer>
            </LeftContainer>
            <RightContainer>
                <RightBackground />
            </RightContainer>
            <Footer />
        </Container>
    );
};

export default Login;
