import styled, { keyframes } from 'styled-components';
export const Container = styled.div`
    max-width: 100vw;
    height: 105vh;
    padding-left: 10rem;
    //scroll-snap-align: center;
    
    background-image: linear-gradient(to right, #03194C, #003CC5);
    @media(max-width: 1080px){
        padding-left: 10vw;
    }
`;
