import React from "react";
import barra from "../../assets/img/barra.png"
import manutencao from "../../assets/img/manutencao.png"
import Insta from "../../assets/img/Insta.png"
import Face from "../../assets/img/Face.png"
import NavBar from "../../components/NavBar";
import Footer from "../../components/Footer";
import {ContentSection, DevTitle, DevText, Card, Row, SocialImg} from "./styles";
import { useEffect, useState } from "react";
import jwtDecode from "jwt-decode";


const Default:React.FC =()=>{
    interface userI {
        name: string | null;
        iat?: number;
        iss?: string;
        picture?: string;
    } 
    const [user, setUser] = useState<userI>({name: null});

    function handleCallbackResponse(response:any) {
        const userObject = jwtDecode(response.credential);

        setUser(userObject as userI);
        document.getElementById("signInDiv")!.hidden = true;
    }
    function handleSignout(){
        setUser({name: null});
        document.getElementById("signInDiv")!.hidden = false;
    }

    useEffect(()=>{
        
        google.accounts!.id.initialize({
            client_id:
                "488131369581-oms526oiaqc55adh4bd8rusd3i8e8qoo.apps.googleusercontent.com",
            callback: handleCallbackResponse,
        });
        const docGetId = document.getElementById("signInDiv")!;
        google.accounts.id.renderButton(docGetId,{
            theme: "outline",
            size: "large",
            type: "standard",
        })

        google.accounts.id.prompt();
    }, [])

    return(
        <div>
            <NavBar/>
                <h1>Google identity services</h1>
                <div id = "signInDiv"></div>
                {user && <img src = {user.picture} alt =""/>}
                <button
                    type = "button"
                    className="cursor-pointer bg-blue-200"
                    onClick={handleSignout}>
                    </button>
            <Footer/>
        </div>
    )
}
export default Default;
