import React, { useEffect } from "react";
import jwtDecode from "jwt-decode";
import styled from "styled-components";

export interface GoogleButtonProps {
  onLoginSuccess: (user: any) => void;
}

const Button = styled.button`
  width: 100%;
  padding: 12px;
  border-radius: 8px;
  border: none;
  background-color: #ea4335;
  color: white;
  font-size: 16px;
  cursor: pointer;
  margin-bottom: 12px;

  &:hover {
    background-color: #d93025;
  }
`;

const GoogleButton: React.FC<GoogleButtonProps> = ({ onLoginSuccess }) => {
  useEffect(() => {
    /* global google */
    google.accounts.id.initialize({
      client_id:
        "488131369581-oms526oiaqc55adh4bd8rusd3i8e8qoo.apps.googleusercontent.com",
      callback: handleCallbackResponse,
    });
  }, []);

  function handleCallbackResponse(response: any) {
    const userObject = jwtDecode(response.credential);
    onLoginSuccess(userObject);
  }

  const handleClick = () => {
    google.accounts.id.prompt();
  };

  return <Button onClick={handleClick}>Sign up with Google</Button>;
};

export default GoogleButton;
