import styled from "styled-components";

export const ContentSection=styled.section`
    text-align:center;
    padding-top: 7.5rem;
    padding-bottom: 7.5rem;
`
export const Card=styled.div`
    border-radius: 20px;
    -webkit-box-shadow: 11px 7px 59px 0px rgba(82,82,82,0.89);
    -moz-box-shadow: 11px 7px 59px 0px rgba(82,82,82,0.89);
    box-shadow: 11px 7px 59px 0px rgba(82,82,82,0.89);
    padding-top: 3rem;
    padding-bottom: 3rem;
    margin: auto;
    width: 800px;
    background-color:#F0F8FF;
    `
export const Title = styled.p`    
    font-family: fantasy;
    font-size: 5rem;
    color: #000000;
    text-shadow: 6px 4px 5px rgba(109,121,134,0.61);
    `
export const Text = styled.p`
    font-size:1.3rem;
    font-weight: 50;
    color: #4F4F4F;
    font-family:fantasy;
`
