import React, { useState } from 'react';
import styled from 'styled-components';

const Nav = styled.nav`
  background-color: #333;
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 1rem;
`;

const NavBrand = styled.a`
  color: #fff;
  font-size: 1.5rem;
  text-decoration: none;
`;

const NavMenu = styled.ul`
  display: flex;
  list-style: none;
  margin: 0;
  padding: 0;
`;

const NavItem = styled.li`
  margin-left: 1rem;
`;

const NavLink = styled.a`
  color: #fff;
  text-decoration: none;
  transition: all 0.2s ease-in-out;

  &:hover {
    color: #ccc;
  }
`;

const NavToggle = styled.button`
  background-color: transparent;
  border: none;
  color: #fff;
  cursor: pointer;
  font-size: 1.5rem;
  margin-right: 1rem;

  &:focus {
    outline: none;
  }
`;

const NavDropdown = styled.ul`
  display: ${({ isOpen }) => (isOpen ? 'block' : 'none')};
  list-style: none;
  margin: 0;
  padding: 0;
  position: absolute;
  top: 100%;
  left: 0;
  background-color: #333;
  width: 100%;
  z-index: 1;
`;

const NavDropdownItem = styled.li`
  margin: 0;
`;

const NavDropdownLink = styled.a`
  color: #fff;
  display: block;
  padding: 1rem;
  text-decoration: none;
  transition: all 0.2s ease-in-out;

  &:hover {
    background-color: #ccc;
    color: #333;
  }
`;

const Navbar = () => {
  const [isOpen, setIsOpen] = useState(false);

  const toggleNav = () => {
    setIsOpen(!isOpen);
  };

  return (
    <Nav>
      <NavBrand href="#">Logo</NavBrand>
      <NavToggle onClick={toggleNav}>{isOpen ? '×' : '☰'}</NavToggle>
      <NavMenu>
        <NavItem>
          <NavLink href="#">Home</NavLink>
        </NavItem>
        <NavItem>
          <NavLink href="#">About</NavLink>
        </NavItem>
        <NavItem>
          <NavLink href="#">Contact</NavLink>
        </NavItem>
        <NavItem>
          <NavLink href="#">Dropdown</NavLink>
          <NavDropdown isOpen={isOpen}>
            <NavDropdownItem>
              <NavDropdownLink href="#">Link 1</NavDropdownLink>
            </NavDropdownItem>
            <NavDropdownItem>
              <NavDropdownLink href="#">Link 2</NavDropdownLink>
            </NavDropdownItem>
            <NavDropdownItem>
              <NavDropdownLink href="#">Link 3</NavDropdownLink>
            </NavDropdownItem>
          </NavDropdown>
        </NavItem>
      </NavMenu>
    </Nav>
  );
};

export default Navbar;



