import React, { useEffect, useState } from "react";
import jwtDecode from "jwt-decode";
import styled from "styled-components";
import NavBar from "../../components/NavBar";
import Footer from "../../components/Footer";
import { Container as StyledContainer } from "./styles";

// Define a interface para o usuário
export interface userI {
  name: string | null;
  iat?: number;
  iss?: string;
  picture?: string;
}

// Styled Components para o layout
const Container = styled.div`
  display: flex;
  height: 100vh;
`;

const LeftContainer = styled.div`
  flex: 1;
  display: flex;
  justify-content: center;
  align-items: center;
  background-color: #f9f9f9;
`;

const FormContainer = styled.div`
  width: 300px;
  text-align: center;
`;

const Title = styled.h2`
  margin-bottom: 24px;
  font-size: 24px;
  color: #333;
`;

const Button = styled.button`
  width: 100%;
  padding: 12px;
  border-radius: 8px;
  border: none;
  background-color: #4f46e5;
  color: white;
  font-size: 16px;
  cursor: pointer;
  margin-bottom: 12px;

  &:hover {
    background-color: #4338ca;
  }
`;

const RightContainer = styled.div`
  flex: 1;
  background-color: #4f46e5;
  display: flex;
  align-items: center;
  justify-content: center;
  padding: 24px;
`;

const RightBackground = styled.div`
  width: 100%;
  height: 100%;
  background-image: url("https://source.unsplash.com/random");
  background-size: cover;
  background-position: center;
  border-radius: 12px;
`;

const Login: React.FC = () => {
  const [user, setUser] = useState<userI>({ name: null });

  // Callback quando o login do Google é bem-sucedido
  function handleCallbackResponse(response: any) {
    console.log("Encoded JWT ID token:" + response.credential);
    const userObject = jwtDecode(response.credential);
    console.log(userObject);

    setUser(userObject as userI);
    document.getElementById("signInDiv")!.hidden = true;
    localStorage.setItem("token", JSON.stringify(userObject));
  }

  // Função para logout
  function handleSignout() {
    setUser({ name: null });
    document.getElementById("signInDiv")!.hidden = false;
    localStorage.removeItem("token");
  }

  useEffect(() => {
    /* global google */
    google.accounts.id.initialize({
      client_id:
        "488131369581-oms526oiaqc55adh4bd8rusd3i8e8qoo.apps.googleusercontent.com",
      callback: handleCallbackResponse,
    });
    const docGetId = document.getElementById("signInDiv")!;
    google.accounts.id.renderButton(docGetId, {
      theme: "outline",
      size: "medium",
      type: "standard",
    });

    google.accounts.id.prompt();
  }, []);

  return (
    <div>
      <NavBar />
      <StyledContainer>
        <Container>
          <LeftContainer>
            <FormContainer>
              <Title>Welcome back!</Title>
              {/* Botão de login do Google */}
              <div id="signInDiv"></div>
              {Object.keys(user).length !== 0 && (
                <Button onClick={() => handleSignout()}>Sign out</Button>
              )}
              {user && user.name && (
                <div>
                  <img src={user.picture} alt={user.name} />
                  <h3>{user.name}</h3>
                </div>
              )}
            </FormContainer>
          </LeftContainer>
          <RightContainer>
            <RightBackground />
          </RightContainer>
        </Container>
      </StyledContainer>
      <Footer />
    </div>
  );
};

export default Login;
