import React, { useState } from "react";
import { FaBars, FaTimes } from "react-icons/fa";
import styled from "styled-components";

const HeaderWrapper = styled.header`
  display: flex;
  justify-content: space-between;
  align-items: center;
  height: 80px;
  padding: 0 30px;
  background-color: #f8f8f8;
  color: #333;
`;

const Logo = styled.a`
  font-size: 24px;
  font-weight: bold;
`;

const Nav = styled.nav`
  display: flex;
  align-items: center;
`;

const NavMenu = styled.ul<{ showMenu: boolean }>`
  display: flex;
  list-style: none;
  margin: 0;
  padding: 0;
  height: auto;
  overflow: hidden;
  flex-direction: column;
  background-color: #f8f8f8;
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  z-index: 1;
  transition: all 0.3s ease-in-out;

  @media (min-width: 1080px) {
    position: static;
    height: 100%;
    flex-direction: row;
  }

  ${({ showMenu }) =>
    showMenu &&
    `
      overflow: auto;
      height: 100vh;
      align-items: center;

      @media (min-width: 1080px) {
        display: flex;
        height: auto;
        overflow: visible;
        align-items: unset;
      }
    `}
`;

const NavItem = styled.li`
  margin: 0;
  padding: 15px;
  text-align: center;
  border-bottom: 1px solid #ccc;

  @media (min-width: 1080px) {
    border-bottom: none;
    padding: 0 15px;
  }
`;

const NavLink = styled.a`
  color: #333;
  text-decoration: none;

  &:hover {
    color: #666;
  }
`;

const MenuIcon = styled.button`
  display: flex;
  align-items: center;
  justify-content: center;
  border: none;
  background-color: transparent;
  color: #333;
  font-size: 24px;

  @media (min-width: 1080px) {
    display: none;
  }
`;

const CloseIcon = styled.button`
  position: absolute;
  top: 20px;
  right: 20px;
  border: none;
  background-color: transparent;
  color: #333;
  font-size: 24px;

  @media (max-width: 1080px) {
    position: absolute;
    top: 50%;
    left: 0;
    right: 0;
    transform: translateY(-50%);
  }
`;

const NavBar: React.FC = () => {
  const [showMenu, setShowMenu] = useState(false);

  const handleMenuClick = () => {
    setShowMenu(!showMenu);
  };

  const handleCloseClick = () => {
    setShowMenu(false);
  };

  return (
    <HeaderWrapper>
      <Logo>EngComp</Logo>
      <Nav>
        <NavMenu showMenu={showMenu}>
          <CloseIcon onClick={handleCloseClick}>
            <FaTimes />
          </CloseIcon>
          <NavItem>
            <NavLink href="#">Home</NavLink>
          </NavItem>
          <NavItem>
            <NavLink href="#">SAEComp</NavLink>
          </NavItem>
          <NavItem>
            <NavLink href="#">ÃŠnfases</NavLink>
          </NavItem>
          <NavItem>
            <NavLink href="#">Notas</NavLink>
          </NavItem>
          <NavItem>
            <NavLink href="#">Manual do Bixo</NavLink>
          </NavItem>
        </NavMenu>
        <MenuIcon onClick={handleMenuClick}>
          <FaBars />
        </MenuIcon>
      </Nav>
    </HeaderWrapper>
  );
};

export default NavBar;
