import styled from 'styled-components';

export const CardDiv = styled.div`
    display:flex;
    flex-direction: column;
    @media (max-width: 1920px){
        img{
        width : 15vw;
        height: 18vw;
        }
    }
`  
export const WhatImg = styled.img`
    width: 240px;
    height: 240px;
`
export const DivText = styled.div`
    margin-top:1rem;
`
export const Text = styled.p`
    color: #828282;
    font-size: 16px;
    font-weight: 700;
    font-family: Oxygen;
    text-align: center;
`
export const VendasImg = styled.div`
    margin-top: 1.3vw;

    position: relative;
    scroll-snap-align: center;
    @media (max-width: 1920px){
        img{
        width : 15vw;
        height: 18vw;
        }
    }
    
`;
