import React, { useState } from "react";
import { FaBars } from "react-icons/fa";
import styled from "styled-components";

const HeaderWrapper = styled.header`
  display: flex;
  justify-content: space-between;
  align-items: center;
  height: 80px;
  padding: 0 30px;
  background-color: #f8f8f8;
  color: #333;
`;

const Logo = styled.a`
  font-size: 24px;
  font-weight: bold;
`;

const Nav = styled.nav`
  display: flex;
  align-items: center;
`;

const NavMenu = styled.ul<{ showMenu: boolean }>`
  display: flex;
  list-style: none;
  margin: 0;
  padding: 0;
  height: auto;
  overflow: hidden;
  flex-direction: column;
  background-color: #f8f8f8;

  @media (min-width: 1080px) {
    flex-direction: row;
    height: 100%;
  }

  ${({ showMenu }) =>
    showMenu &&
    `
      overflow: auto;

      @media (min-width: 1080px) {
        display: flex;
        height: auto;
        overflow: visible;
      }
    `}
`;

const NavItem = styled.li`
  margin: 0;
  padding: 15px;
  text-align: center;
  border-bottom: 1px solid #ccc;

  @media (min-width: 1080px) {
    border-bottom: none;
    padding: 0 15px;
  }
`;

const NavLink = styled.a`
  color: #333;
  text-decoration: none;

  &:hover {
    color: #666;
  }
`;

const MenuIcon = styled.button`
  display: flex;
  align-items: center;
  justify-content: center;
  border: none;
  background-color: transparent;
  color: #333;
  font-size: 24px;

  @media (min-width: 1080px) {
    display: none;
  }
`;

interface MenuItem {
  label: string;
  url: string;
}

interface Props {
  logo: string;
  menuItems: MenuItem[];
}

const Header: React.FC<Props> = ({ logo, menuItems }) => {
  const [showMenu, setShowMenu] = useState(false);

  const toggleMenu = () => setShowMenu(!showMenu);

  return (
    <HeaderWrapper>
      <Logo href={logo}>Logo</Logo>
      <Nav>
        <MenuIcon onClick={toggleMenu}>
          <FaBars />
        </MenuIcon>
        <NavMenu showMenu={showMenu}>
          {menuItems.map(({ label, url }) => (
            <NavItem key={url}>
              <NavLink href={url}>{label}</NavLink>
            </NavItem>
          ))}
        </NavMenu>
      </Nav>
    </HeaderWrapper>
  );
};

export default Header;
