import React, {useState} from "react";
import OrangePinkButton from "../Button";
import { LoginDiv, LogoDiv, NavBarContainer, NavItem, NavList, HamburgerMenu, HamburgerIcon, HamburgerMenuList, HamburgerMenuItem } from "./styles";

const NavBar:React.FC = ()=>{
  const [scrollY, setScrollY] = React.useState(0);
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  function handleScroll() {
    // Change Header background color.
    setScrollY(window.pageYOffset);
  }

  React.useEffect(() => {
    window.addEventListener("scroll", handleScroll);
    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, []);

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  }

  return (
    <NavBarContainer scrollY={scrollY}>
      <LogoDiv><a href="#">ENG<span>COMP</span></a></LogoDiv>
      <HamburgerMenu onClick={toggleMenu}>
        <HamburgerIcon />
      </HamburgerMenu>
      <NavList>
        <NavItem><a href="/">Home</a></NavItem>
        <NavItem><a href="/SAEComp">SAEComp</a></NavItem>
        <NavItem><a href="/Default">Ênfases</a></NavItem>
        <NavItem><a href="/Default">Notas</a></NavItem>
        <NavItem><a href="/Manual-Bixo">Manual do Bixo</a></NavItem>
      </NavList>
      <LoginDiv>Aluno? <OrangePinkButton text="Login"/></LoginDiv>
      {isMenuOpen && (
        <HamburgerMenuList>
          <HamburgerMenuItem><a href="/">Home</a></HamburgerMenuItem>
          <HamburgerMenuItem><a href="/SAEComp">SAEComp</a></HamburgerMenuItem>
          <HamburgerMenuItem><a href="/Default">Ênfases</a></HamburgerMenuItem>
          <HamburgerMenuItem><a href="/Default">Notas</a></HamburgerMenuItem>
          <HamburgerMenuItem><a href="/Manual-Bixo">Manual do Bixo</a></HamburgerMenuItem>
        </HamburgerMenuList>
      )}
    </NavBarContainer>
  );
}

export default NavBar;
