import React, { useState } from 'react';
import styled from 'styled-components';
import { FaBars, FaTimes } from 'react-icons/fa';
const Nav = styled.nav`
  display: flex;
  justify-content: space-between;
  align-items: center;
  height: 80px;
  padding: 0 2rem;
  background-color: #fff;
  position: fixed;
  top: 0;
  width: 100%;
  z-index: 999;
`;

const Logo = styled.div`
  color: #000;
  font-size: 2rem;
  font-weight: 700;
`;

const MenuBars = styled(FaBars)`
  display: none;

  @media screen and (max-width: 768px) {
    display: block;
    font-size: 2rem;
    cursor: pointer;
  }
`;

const NavMenu = styled.div`
  display: flex;
  align-items: center;
  margin-right: -48px;

  @media screen and (max-width: 768px) {
    display: none;
  }
`;

const NavMenuLinks = styled.a`
  color: #000;
  display: flex;
  align-items: center;
  text-decoration: none;
  padding: 0 1rem;
  height: 100%;
  cursor: pointer;

  &:hover {
    border-bottom: 2px solid #000;
  }
`;

const NavBtn = styled.div`
  display: flex;
  align-items: center;
  margin-right: 24px;

  @media screen and (max-width: 768px) {
    display: none;
  }
`;
const NavBar = () => {
  const [open, setOpen] = useState(false);

  const handleClick = () => {
    setOpen(!open);
  };

  return (
    <Nav>
      <Logo>Logo</Logo>
      <MenuBars onClick={handleClick} />
      <NavMenu>
        <NavMenuLinks>Link 1</NavMenuLinks>
        <NavMenuLinks>Link 2</NavMenuLinks>
        <NavMenuLinks>Link 3</NavMenuLinks>
        <NavMenuLinks>Link 4</NavMenuLinks>
      </NavMenu>
      <NavBtn>Button</NavBtn>
      {open && (
        <NavMenu>
          <NavMenuLinks>Link 1</NavMenuLinks>
          <NavMenuLinks>Link 2</NavMenuLinks>
          <NavMenuLinks>Link 3</NavMenuLinks>
          <NavMenuLinks>Link 4</NavMenuLinks>
          <NavMenuLinks onClick={handleClick}>
            <FaTimes />
          </NavMenuLinks>
        </NavMenu>
      )}
    </Nav>
  );
};

export default NavBar;
