import React, { useState } from 'react';
import styled from 'styled-components';
import { FaBars, FaTimes } from 'react-icons/fa';

interface Link {
  label: string;
  url: string;
}

const links: Link[] = [
  { label: 'Home', url: '/' },
  { label: 'SAEComp', url: '/Default' },
  { label: 'Enfâses', url: '/Default' },
  { label: 'Notas', url: '/SAEComp' },
  { label: 'Manual', url: '//Manual-Bixo' },
];

const Navbar: React.FC = () => {
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);

  const toggleMobileMenu = () => {
    setIsMobileMenuOpen((prev) => !prev);
  };

  return (
    <NavbarWrapper>
      <NavbarBrand href="/">MyApp</NavbarBrand>
      <NavbarLinks isMobileMenuOpen={isMobileMenuOpen}>
        {links.map((link) => (
          <NavbarLink key={link.url}>
            <a href={link.url}>{link.label}</a>
          </NavbarLink>
        ))}
      </NavbarLinks>
      <MenuButton onClick={toggleMobileMenu} isMobileMenuOpen={isMobileMenuOpen}>
        {isMobileMenuOpen ? <FaTimes /> : <FaBars />}
      </MenuButton>
    </NavbarWrapper>
  );
};

const NavbarWrapper = styled.nav`
  display: flex;
  justify-content: space-between;
  align-items: center;
  background-color: #333;
  color: #fff;
  padding: 1rem;
`;

const NavbarBrand = styled.a`
  font-size: 1.5rem;
  font-weight: bold;
  color: #fff;
  text-decoration: none;
`;

interface NavbarLinksProps {
  isMobileMenuOpen: boolean;
}

const NavbarLinks = styled.ul<NavbarLinksProps>`
  display: ${({ isMobileMenuOpen }) => (isMobileMenuOpen ? 'flex' : 'none')};
  flex-direction: column;
  align-items: flex-end;
  margin: 0;
  padding: 0;
  list-style: none;

  @media screen and (min-width: 1080px) {
    display: flex;
    flex-direction: row;
    align-items: center;
  }
`;

const NavbarLink = styled.li`
  margin: 0 0.5rem;

  & a {
    color: #fff;
    text-decoration: none;

    &:hover {
      text-decoration: underline;
    }
  }

  @media screen and (min-width: 1080px) {
    margin: 0 1rem;
  }
`;

const MenuButton = styled.button<NavbarLinksProps>`
  display: flex;
  justify-content: center;
  align-items: center;
  background-color: transparent;
  color: #fff;
  font-size: 1.5rem;
  border: none;
  cursor: pointer;
  margin: 0;
  padding: 0.5rem;

  @media screen and (min-width: 1080px) {
    display: none;
  }

  ${({ isMobileMenuOpen }) =>
    isMobileMenuOpen &&
    `
      position: absolute;
      top: 0.5rem;
      right: 0.5rem;
      font-size: 2rem;
      padding: 0.25rem;
      transition: transform 0.3s ease-in-out;

      & svg {
        transform: rotate(90deg);
      }
    `}
`;

export default Navbar;





