import React, {useEffect, useRef, useState} from "react";
import OrangePinkButton from "../Button";
import { LoginDiv, LogoDiv, NavBarContainer, NavItem, NavList} from "./styles";


const NavBar:React.FC = () =>{
        type Coords = {
                x: number;
                y: number;
              };

              function getScrollCoordinates (): Coords {
                return {
                  x: window.scrollX,
                  y: window.scrollY,
                };
              }

              function writeScrollCoordinates (elementContainer: HTMLElement, coords: Coords): void {
                elementContainer.textContent = JSON.stringify(coords, null, 2);
              }

              function handleScroll (event: Event): void {
                //                   ^^^^^^^^^^^^
                // The scroll event is just an instance of the base Event class
                writeScrollCoordinates(document.getElementById('coords')!, getScrollCoordinates());
              }

              window.addEventListener('scroll', handleScroll);
        return(
           <NavBarContainer className="Nav-bar">
                   <LogoDiv><a href="#">ENG<span>COMP</span></a></LogoDiv>
                   <NavList> 
                        <NavItem><a href="#">O Curso</a></NavItem>
                        <NavItem><a href="/Extracurriculares">Extracurriculares</a></NavItem>
                        <NavItem><a href="/Enfases">Enfases</a></NavItem>
                        <NavItem><a href="/Notas-Corte">Notas</a></NavItem>
                        <NavItem><a href="/Manual-Bixo">Manual do Bixo</a></NavItem>
                   </NavList>
                   <LoginDiv>Ã‰ aluno? <OrangePinkButton text="Login"/></LoginDiv>
           </NavBarContainer>
        );

}

export default NavBar;
