import React, { useEffect, useState } from "react";
import { useLocation } from "react-router-dom";
import NavBar from "../../components/NavBar";
import Footer from "../../components/Footer";
import jwtDecode from "jwt-decode";
import { Container, LoginCard, ErrorMessage } from "./styles"; // Adicione ErrorMessage

export interface userI {
  name: string | null;
  iat?: number;
  iss?: string;
  picture?: string;
  email?: string;
}

const Login: React.FC = () => {
  const location = useLocation();
  const [user, setUser] = useState<userI>({ name: null });
  const [isAuthenticated, setIsAuthenticated] = useState(
    localStorage.getItem("token") !== null
  );
  const [redirectTo, setRedirectTo] = useState<string | null>(null);

  // Novo estado para a mensagem de erro
  const [errorMessage, setErrorMessage] = useState<string | null>(null);

  useEffect(() => {
    // ...

    if (!isAuthenticated && typeof google !== "undefined" && google.accounts) {
      google.accounts.id.initialize({
        client_id:
          "488131369581-oms526oiaqc55adh4bd8rusd3i8e8qoo.apps.googleusercontent.com",
        callback: handleCallbackResponse,
      });

      const docGetId = document.getElementById("signInDiv")!;
      google.accounts.id.renderButton(docGetId, {
        theme: "outline",
        size: "medium",
        type: "standard",
      });

      google.accounts.id.prompt();
    }
  }, [isAuthenticated, redirectTo]);

  const handleSignout = () => {
    setUser({ name: null });
    localStorage.removeItem("token");
    setIsAuthenticated(false);
    window.location.href = "/Login";
  };

  return (
    <div>
      <NavBar />
      <Container>
        <LoginCard>
          <h2>Login</h2>
          {isAuthenticated ? (
            <div>
              <button onClick={handleSignout}>Sign out</button>
            </div>
          ) : null}
          {!isAuthenticated ? <div id="signInDiv"></div> : null}
          
          {/* Exibição da mensagem de erro */}
          {errorMessage && <ErrorMessage>{errorMessage}</ErrorMessage>}
        </LoginCard>
      </Container>
      <Footer />
    </div>
  );
};

export default Login;
