import React from "react";
import { Container, Text, Container2 } from "./style";
import { FaCaretDown, FaEnvelope, FaHome, FaPhone, FaSmile } from "react-icons/fa";
import img1 from '../../assets/img/background1.jpg';
import img2 from '../../assets/img/background2.jpg'
import img3 from '../../assets/img/background2.jpg'
import { DefaultSubtitle } from "../../pages/Default/styles";

interface  Props{
    type:string;
    text:string;
};
const ContactDir:React.FC<Props> = ({text, type})=>{
  
    let img = <FaSmile/>;
    if(type == "Email"){
        img = <FaEnvelope/>
    }
    else{
        if(type == "Telefone"){
            img = <FaPhone/>
        }

        else{
            if(type == "Endereço"){
                img = <FaHome/>
            }
        }
    }
    
    return (
        
        <Container>
            <Container2>
                <div style={{padding: "2rem"}}>
                    {img}
                </div>
            </Container2>
            <Container2>
                    <DefaultSubtitle>{type}</DefaultSubtitle>
                <Text>
                    {text}
                </Text>
                
            </Container2>        
        </Container>        
            
            
        
        
    )
}

export default ContactDir;
