import styled from 'styled-components';
import dark from '../../themes/dark';

interface Props {
  scrollY: number;
  showMenu: boolean;
}

export const NavBarContainer = styled.div<Props>`
  position: fixed;
  top: 0;
  z-index: 1;
  width: 100%;
  height: 80px;
  padding: 0 10rem;
  background-color: ${({ scrollY }) =>
    scrollY > document.documentElement.clientHeight
      ? 'rgba(0,0,0,0.8)'
      : 'rgba(0,0,0,0.2)'};
  box-shadow: rgba(255, 255, 255, 0.1) 0px 1px 1px 0px inset,
    rgba(50, 50, 93, 0.25) 0px 50px 100px -20px,
    rgba(0, 0, 0, 0.3) 0px 30px 60px -30px;
  color: ${dark.text};
  display: flex;
  justify-content: center;
  transition: background-color 0.3s ease-in-out;
  > div {
    width: 100%;
    height: 80px;
    font-weight: lighter;
    /* border:1px solid red; */
  }
`;

export const NavList = styled.div<Props>`
  @media (max-width: 1080px) {
    display: ${({ showMenu }) => (showMenu ? 'flex' : 'none')};
    position: absolute;
    top: 80px;
    left: 0;
    width: 100%;
    height: calc(100vh - 80px);
    flex-direction: column;
    justify-content: center;
    align-items: center;
    background-color: rgba(0, 0, 0, 0.8);
    transition: all 0.5s ease-in-out;
  }
  display: flex;
  /*align-items: center;*/
  justify-content: space-around;
`;

export const NavItem = styled.div`
  width: 10vw;
  font-weight: lighter;
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 0.9rem;
  a {
    text-decoration: none;
    color: ${dark.text};
    transition: text-shadow 0.3s, color 0.3s;
    &:hover {
      color: rgb(238, 9, 121);
      text-shadow: 0px 0px 25px white;
    }
  }
`;

export const LogoDiv = styled.div`
  display: flex;
  align-items: center;
  font-size: 2.5rem;
  cursor: pointer;
  transition: transform 0.2s;
  a {
    color: white;
    text-decoration: none;
    transition: color 0.2s;
  }
  a span {
    font-weight: bold;
  }
  &:hover {
    transform: scale(1.05);
    a {
      color: #00efff;
    }
  }
`;

export const LoginDiv = styled.div`
  display: flex;
  align-items: center;
  justify-content: flex-end;
  font-size: 0.7rem;
  @media (max-width: 1080px) {
    display: none;
  }
  button {
    width: 6.25rem;
    height: 2.5rem;
    margin-left: 1rem;
    font-size: 1rem;
  }
`;

export const MenuIcon = styled.div<Props>`
  cursor: pointer;
  display: none;
  @media (max-width: 1080px) {
    display: flex;
    position: fixed;
    top: 1.5rem;
    right: 2rem;
    z-index: 10;
    flex-direction: column;
    justify-content: space-around;
    width: 2rem;
    height: 2rem;
    background: transparent;
    border: none;
    outline: none;
  }
  div {
    width: 2rem;
    height: 0.25rem;
    background: ${({ scrollY }) =>
      scrollY > document.documentElement.clientHeight
        ? dark.text
        : light.text};
    transition: all 0.3s ease-in-out;
    &:nth-child(1) {
      transform: ${({ open }) => (open ? "rotate(45deg)" : "rotate(0)")};
    }
    &:nth-child(2) {
      transform: ${({ open }) =>
        open ? "translateX(100%)" : "translateX(0)"};
      opacity: ${({ open }) => (open ? 0 : 1)};
    }
    &:nth-child(3) {
      transform: ${({ open }) => (open ? "rotate(-45deg)" : "rotate(0)")};
    }
  }
`;

export const MenuList = styled.div<Props>`
  display: flex;
  flex-direction: column;
  justify-content: center;
  background-color: ${({ scrollY }) =>
    scrollY > document.documentElement.clientHeight
      ? "rgba(0, 0, 0, 0.8)"
      : "rgba(0, 0, 0, 0.2)"};
  position: fixed;
  top: 0;
  right: 0;
  height: 100vh;
  width: 100vw;
  transform: ${({ open }) => (open ? "translateX(0)" : "translateX(100%)")};
  transition: transform 0.3s ease-in-out;
  z-index: 9;
  > * {
    margin: 1rem 0;
    text-align: center;
    font-size: 1.5rem;
    font-weight: bold;
    text-transform: uppercase;
    letter-spacing: 0.5rem;
    color: white;
    text-decoration: none;
    transition: color 0.3s linear;
    cursor: pointer;
    &:hover {
      color: ${({ theme }) => theme.colors.primary};
    }
  }
`;

export const MenuLink = styled.a`
  font-size: 1.3rem;
  color: white;
  text-decoration: none;
  transition: color 0.3s;

  &:hover {
    color: #ee0979;
  }
`;
