import React, { useState } from "react";
import { useLocation } from "react-router-dom";
import NavBar from "../../components/NavBar";
import Footer from "../../components/Footer";
import jwtDecode from "jwt-decode";
import { Container, LoginCard } from "./styles";

export interface userI {
  name: string | null;
  iat?: number;
  iss?: string;
  picture?: string;
  email?: string;
}

const Login: React.FC = () => {
  const location = useLocation();
  const [user, setUser] = useState<userI>({ name: null });
  const [isAuthenticated, setIsAuthenticated] = useState(
    localStorage.getItem("token") !== null
  );
  const [redirectTo, setRedirectTo] = useState<string | null>(null);
  const [errorMessage, setErrorMessage] = useState(""); 

  function handleCallbackResponse(response: any) {
    console.log("Encoded JWT ID token:" + response.credential);
    const userObject = jwtDecode(response.credential);
    console.log(userObject);

    if (isUserObject(userObject) && userObject.email?.endsWith("@usp.br")) {
      setUser(userObject as userI);
      localStorage.setItem("token", "userObject");
      setIsAuthenticated(true);

      if (redirectTo) {
        window.location.href = redirectTo;
      } else {
        window.location.href = "/";
      }
    } else {
      setErrorMessage("Você deve usar um email @usp.br para fazer login.");
    }
  }

  const isUserObject = (object: any): object is userI => {
    return "email" in object;
  };

  const handleSignout = () => {
    setUser({ name: null });
    localStorage.removeItem("token");
    setIsAuthenticated(false);

    window.location.href = "/Login";
  };

  return (
    <div>
      <NavBar />
      <Container>
        {isAuthenticated ? (
          <div>
            <button onClick={handleSignout}>Sign out</button>
          </div>
        ) : null}
        {!isAuthenticated ? <div id="signInDiv"></div> : null}
        
        {errorMessage && <p className="error-message">{errorMessage}</p>}
      </Container>
      <Footer />
    </div>
  );
}

export default Login;
