import React, { useEffect, useState } from "react";
import OrangePinkButton from "../Button";
import {
  NavBarContainer,
  NavList,
  NavItem,
  LogoDiv,
  LoginDiv,
  MenuIcon,
  MenuList,
  MenuLink,
} from "./styles";

const NavBar: React.FC = () => {
  const [scrollY, setScrollY] = React.useState(0);
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  function handleScroll() {
    setScrollY(window.pageYOffset);
  }

  useEffect(() => {
    window.addEventListener("scroll", handleScroll);
    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, []);

  return (
    <NavBarContainer scrollY={scrollY}>
      <LogoDiv>
        <a href="#">ENG<span>COMP</span></a>
      </LogoDiv>

      {isMenuOpen ? (
        <MenuList>
          <MenuLink href="/">Home</MenuLink>
          <MenuLink href="/SAEComp">SAEComp</MenuLink>
          <MenuLink href="/Default">Ênfases</MenuLink>
          <MenuLink href="/Default">Notas</MenuLink>
          <MenuLink href="/Manual-Bixo">Manual do Bixo</MenuLink>
        </MenuList>
      ) : (
        <NavList>
          <NavItem>
            <a href="/">Home</a>
          </NavItem>
          <NavItem>
            <a href="/SAEComp">SAEComp</a>
          </NavItem>
          <NavItem>
            <a href="/Default">Ênfases</a>
          </NavItem>
          <NavItem>
            <a href="/Default">Notas</a>
          </NavItem>
          <NavItem>
            <a href="/Manual-Bixo">Manual do Bixo</a>
          </NavItem>
        </NavList>
      )}

      <LoginDiv>
        Aluno? <OrangePinkButton text="Login" />
      </LoginDiv>

      <MenuIcon onClick={() => setIsMenuOpen(!isMenuOpen)}>
        <div />
        <div />
        <div />
      </MenuIcon>
    </NavBarContainer>
  );
};

export default NavBar;
