import React from "react";
import NavBar from "../../components/NavBar";
import Footer from "../../components/Footer";
import { useEffect, useState } from "react";
import jwtDecode from "jwt-decode";
import { Container, LoginCard } from "./styles";

export interface userI {
  name: string | null;
  iat?: number;
  iss?: string;
  picture?: string;
  email?: string; // Adicione a propriedade "email" ao tipo userI
}

const Login: React.FC = () => {
  const [user, setUser] = useState<userI>({ name: null });
  const [isAuthenticated, setIsAuthenticated] = useState(
    localStorage.getItem("token") !== null
  );

  function handleCallbackResponse(response: any) {
    console.log("Encoded JWT ID token:" + response.credential);
    const userObject = jwtDecode(response.credential);
    console.log(userObject);

    if (isUserObject(userObject) && userObject.email?.endsWith("@usp.br")) {
      setUser(userObject);
      localStorage.setItem("token", "userObject");
      setIsAuthenticated(true);
    } else {
      // Caso o usuário não tenha um e-mail da USP, você pode lidar com isso aqui.
      // Por exemplo, exibir uma mensagem de erro ou redirecioná-lo para uma página de erro.
    }
  }

  // Verificação de tipo para userObject
  function isUserObject(object: any): object is userI {
    return "email" in object;
  }

  function handleSignout() {
    setUser({ name: null });
    localStorage.removeItem("token");
    setIsAuthenticated(false);
  }

  useEffect(() => {
    /* global google */
    google.accounts!.id.initialize({
      client_id:
        "488131369581-oms526oiaqc55adh4bd8rusd3i8e8qoo.apps.googleusercontent.com",
      callback: handleCallbackResponse,
    });

    if (!isAuthenticated) {
      const docGetId = document.getElementById("signInDiv")!;
      google.accounts.id.renderButton(docGetId, {
        theme: "outline",
        size: "medium",
        type: "standard",
      });

      google.accounts.id.prompt();
    }
  }, [isAuthenticated]);

  return (
    <div>
      <NavBar />
      <Container>
        <LoginCard>
          {isAuthenticated ? (
            <div>
              <button onClick={() => handleSignout()}>Sign out</button>
            </div>
          ) : null} {/* Não renderiza nada quando autenticado */}
          {!isAuthenticated ? (
            <div id="signInDiv"></div>
          ) : null} {/* Não renderiza nada quando não autenticado */}
        </LoginCard>
      </Container>
      <Footer />
    </div>
  );
};

export default Login;
