import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import jwtDecode from "jwt-decode";
import { Container } from "./styles";

const LeftContainer = styled.div`
  flex: 1;
  display: flex;
  justify-content: center;
  align-items: center;
  background-color: #f9f9f9;

  @media (max-width: 768px) {
    width: 100%;
  }
`;

const FormContainer = styled.div`
  width: 300px;
  text-align: center;
`;

const Title = styled.h2`
  margin-bottom: 24px;
  font-size: 24px;
  color: #333;
`;

const Input = styled.input`
  width: 100%;
  padding: 12px;
  margin-bottom: 16px;
  border-radius: 8px;
  border: 1px solid #ccc;
`;

const Button = styled.button`
  width: 100%;
  padding: 12px;
  border-radius: 8px;
  border: none;
  background-color: #4f46e5;
  color: white;
  font-size: 16px;
  cursor: pointer;
  margin-bottom: 12px;

  &:hover {
    background-color: #4338ca;
  }
`;

const Divider = styled.div`
  display: flex;
  align-items: center;
  text-align: center;
  margin: 16px 0;

  &::before,
  &::after {
    content: '';
    flex: 1;
    border-bottom: 1px solid #ccc;
  }

  &::before {
    margin-right: .25em;
  }

  &::after {
    margin-left: .25em;
  }

  span {
    color: #666;
    font-size: 14px;
  }
`;

const GoogleButtonContainer = styled.div`
  width: 100%;
  padding: 12px;
  border-radius: 8px;
  border: 1px solid #ccc;  // Contorno claro
  text-align: center;
  cursor: pointer;
  margin-bottom: 12px;
  background-color: white;

  & > div {
    display: flex;
    justify-content: center;
    align-items: center;
    height: 100%;
    width: 100%;
    border-radius: 8px;
    background: transparent;
  }

  &:hover {
    border-color: #888;  // Muda a cor do contorno ao passar o mouse
  }
`;


const RememberMeContainer = styled.div`
  display: flex;
  align-items: center;
  justify-content: space-between;
  margin-bottom: 16px;
`;

const RememberMe = styled.label`
  display: flex;
  align-items: center;

  input {
    margin-right: 8px;
  }
`;

const RightContainer = styled.div`
  flex: 1;
  background-color: #4f46e5;
  display: flex;
  align-items: center;
  justify-content: center;
  padding: 24px;

  @media (max-width: 768px) {
    display: none;
  }
`;

const RightBackground = styled.div`
  width: 100%;
  height: 100%;
  background-image: url('https://source.unsplash.com/random');
  background-size: cover;
  background-position: center;
  border-radius: 12px;
`;

export interface userI {
    name: string | null;
    iat?: number;
    iss?: string;
    picture?: string;
}

const Login: React.FC = () => {
    const [user, setUser] = useState<userI>({ name: null });

    function handleLoginSuccess(userObject: any) {
        setUser(userObject as userI);
        localStorage.setItem("token", JSON.stringify(userObject));
    }

    function handleCallbackResponse(response: any) {
        console.log("Encoded JWT ID token:" + response.credential);
        const userObject = jwtDecode(response.credential);
        console.log(userObject);

        setUser(userObject as userI);
        localStorage.setItem("token", JSON.stringify(userObject));
    }

    function handleSignout() {
        setUser({ name: null });
        localStorage.removeItem("token");
    }

    useEffect(() => {
        /* global google */
        google.accounts.id.initialize({
            client_id: "488131369581-oms526oiaqc55adh4bd8rusd3i8e8qoo.apps.googleusercontent.com",
            callback: handleCallbackResponse,
        });

        google.accounts.id.renderButton(
            document.getElementById("signInDiv")!,
            {
                theme: "outline",  // Botão com contorno claro
                size: "large",
                text: "signin_with",  // Texto padrão "Sign up with Google"
                shape: "rectangular",
                logo_alignment: "left",  // Logo alinhado à esquerda
                width: "100%",  // O botão ocupa toda a largura do contêiner
                type: "standard",
            }
        );

        google.accounts.id.prompt();
    }, []);

    return (
        <Container>
            <LeftContainer>
                <FormContainer>
                    {user.name ? (
                        <Button onClick={handleSignout}>Sign out</Button>
                    ) : (
                        <>
                            <Title>Bem vindos à Eng Comp!</Title>
                            <Input type="email" placeholder="Enter your email address" />
                            <Input type="password" placeholder="Enter password" />
                            <RememberMeContainer>
                                <RememberMe>
                                    <input type="checkbox" />
                                    <span>Remember me</span>
                                </RememberMe>
                                <a href="/">Forgot your password?</a>
                            </RememberMeContainer>

                            <Divider><span>Or, Login with</span></Divider>

                            {/* Botão do Google estilizado */}
                            <GoogleButtonContainer>
                                <div id="signInDiv"></div>
                            </GoogleButtonContainer>

                            <p>
                                Don't have an account? <a href="/">Register here</a>
                            </p>
                        </>
                    )}
                </FormContainer>
            </LeftContainer>
            <RightContainer>
                <RightBackground />
            </RightContainer>
        </Container>
    );
};

export default Login;
