import styled from 'styled-components';

export const ColLeft = styled.div`
    -webkit-box-flex: 0;
    -ms-flex: 0 0 33.333333%;
    flex: 0 0 33.333333%;
    max-width: 33.333333%;
    padding-right: 15px;
    padding-left: 15px;
    width: 100%;
   
    display:flex;
`
export const ImgExtra = styled.img`
    max-height: 150px !important;
    max-width: 90% !important;
    margin: 0 auto 30px;
    width: auto !important;
`
export const TitleCont = styled.h1`
    float: right;
`
export const TextCont = styled.p`
    float: right;
`
