import React, { useState } from "react";
import styled from "styled-components";

const Nav = styled.nav`
  background: #fff;
  height: 80px;
  display: flex;
  justify-content: space-between;
  padding: 0.5rem calc((100vw - 1000px) / 2);
  z-index: 12;
  position: fixed;
  width: 100%;

  @media screen and (max-width: 768px) {
    padding: 0;
  }
`;

const NavLogo = styled.a`
  color: #000;
  justify-self: flex-start;
  cursor: pointer;
  font-size: 2rem;
  display: flex;
  align-items: center;
`;

const NavIcon = styled.i`
  margin-right: 0.5rem;
`;

const SidebarLink = styled.a`
  display: flex;
  color: #000;
  justify-content: space-between;
  align-items: center;
  padding: 20px;
  list-style: none;
  height: 60px;
  text-decoration: none;
  font-size: 18px;

  &:hover {
    background: #f9f9f9;
    cursor: pointer;
  }
`;

const SidebarMenu = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  text-align: center;
  background-color: #fff;
  position: fixed;
  top: 0;
  right: ${({ isOpen }) => (isOpen ? "0" : "-100%")};
  height: 100vh;
  width: 100%;
  transition: all 0.3s ease;
  z-index: 999;
`;

const Sidebar = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: space-between;
  height: 100%;
  padding: 1rem;
`;

const SidebarClose = styled.i`
  font-size: 2rem;
  cursor: pointer;
  color: #000;
  margin-top: 1rem;
`;

const NavMenu = styled.div`
  display: flex;
  align-items: center;
  justify-content: space-between;
  width: 70vw;
  white-space: nowrap;

  @media screen and (max-width: 768px) {
    display: none;
  }
`;

const NavMenuLink = styled.a`
  color: #000;
  display: flex;
  align-items: center;
  text-decoration: none;
  padding: 0 1rem;
  height: 100%;
  cursor: pointer;

  &:hover {
    border-bottom: 2px solid #000;
  }
`;

const NavButton = styled.div`
  display: none;

  @media screen and (max-width: 768px) {
    display: block;
    position: absolute;
    top: 0;
    right: 0;
    transform: translate(-100%, 60%);
    font-size: 1.8rem;
    cursor: pointer;
  }
`;

const NavBar: React.FC = () => {
  const [isOpen, setIsOpen] = useState<boolean>(false);

  const handleToggle = () => {
    setIsOpen(!isOpen);
  };

  return (
    <Nav>
      <NavLogo href="#">
        <NavIcon className="fas fa-paw" />
        Logo
      </NavLogo>

      <NavMenu>
        <NavMenuLink href="#">Link 1</NavMenuLink>
        <NavMenuLink href="#">Link 2</NavMenuLink>
        <NavMenuLink href="#">Link 3</NavMenuLink>
        <NavMenuLink href="#">Link 4</NavMenuLink>
      </NavMenu>

      <NavButton onClick={handleToggle}>
        <i className="fas fa-bars" />
      </NavButton>

      <SidebarMenu isOpen={isOpen}>
        <Sidebar>
          <SidebarClose onClick={handleToggle}>
            <i className="fas fa-times" />
          </SidebarClose>

          <SidebarLink href="#">Link 1</SidebarLink>
          <SidebarLink href="#">Link 2</SidebarLink>
          <SidebarLink href="#">Link 3</SidebarLink>
          <SidebarLink href="#">Link 4</SidebarLink>
        </Sidebar>
      </SidebarMenu>
    </Nav>
  );
};

export default NavBar;
