import React, { useEffect, useState } from "react";
import { useLocation } from "react-router-dom";
import NavBar from "../../components/NavBar";
import Footer from "../../components/Footer";
import jwtDecode from "jwt-decode";
import { Container, LoginCard } from "./styles";

export interface userI {
  name: string | null;
  iat?: number;
  iss?: string;
  picture?: string;
  email?: string;
}

const Login: React.FC = () => {
  const location = useLocation();
  const [user, setUser] = useState<userI>({ name: null });
  const [isAuthenticated, setIsAuthenticated] = useState(
    localStorage.getItem("token") !== null
  );
  const [redirectTo, setRedirectTo] = useState<string | null>(null);
  const [errorMessage, setErrorMessage] = useState<string | null>(null); // Adicionando mensagem de erro

  useEffect(() => {
    const handleCallbackResponse = (response: any) => {
      console.log("Encoded JWT ID token:" + response.credential);
      const userObject = jwtDecode(response.credential);
      console.log(userObject);

      if (isUserObject(userObject)) {
        setUser(userObject);
        localStorage.setItem("token", "userObject");
        setIsAuthenticated(true);

        if (userObject.email?.endsWith("@usp.br")) {
          // Email válido
          if (redirectTo) {
            window.location.href = redirectTo;
          } else {
            window.location.href = "/";
          }
        } else {
          // Email inválido
          setErrorMessage("O email deve ser do domínio @usp.br");
        }
      } else {
        console.error("Usuário não tem um e-mail da USP.");
      }
    };

    const isUserObject = (object: any): object is userI => {
      return "email" in object;
    };

    if (!isAuthenticated && typeof google !== "undefined" && google.accounts) {
      google.accounts.id.initialize({
        client_id:
          "488131369581-oms526oiaqc55adh4bd8rusd3i8e8qoo.apps.googleusercontent.com",
        callback: handleCallbackResponse,
      });

      const docGetId = document.getElementById("signInDiv")!;
      google.accounts.id.renderButton(docGetId, {
        theme: "outline",
        size: "medium",
        type: "standard",
      });

      google.accounts.id.prompt();
    }
  }, [isAuthenticated, redirectTo]);

  const handleSignout = () => {
    setUser({ name: null });
    localStorage.removeItem("token");
    setIsAuthenticated(false);
    window.location.href = "/Login";
  };

  return (
    <div>
      <NavBar />
      <Container>
        {isAuthenticated ? (
          <div>
            <button onClick={handleSignout}>Sign out</button>
          </div>
        ) : null}
        {!isAuthenticated ? (
          <div id="signInDiv">
            {errorMessage && (
              <p className="error-message">{errorMessage}</p> // Mostrar mensagem de erro
            )}
          </div>
        ) : null}
      </Container>
      <Footer />
    </div>
  );
};

export default Login;
