import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import jwtDecode from "jwt-decode";
import { Container } from "./styles";

const LeftContainer = styled.div`
  flex: 1;
  display: flex;
  justify-content: center;
  align-items: center;
  background-color: #f9f9f9;

  @media (max-width: 768px) {
    width: 100%;
  }
`;

const FormContainer = styled.div`
  width: 300px;
  text-align: center;
`;

const Title = styled.h2`
  margin-bottom: 24px;
  font-size: 24px;
  color: #333;
`;

const Input = styled.input`
  width: 100%;
  padding: 12px;
  margin-bottom: 16px;
  border-radius: 8px;
  border: 1px solid #ccc;
`;

const Button = styled.button`
  width: 100%;
  padding: 12px;
  border-radius: 8px;
  border: none;
  background-color: #4f46e5;
  color: white;
  font-size: 16px;
  cursor: pointer;
  margin-bottom: 12px;

  &:hover {
    background-color: #4338ca;
  }
`;

const RememberMeContainer = styled.div`
  display: flex;
  align-items: center;
  justify-content: space-between;
  margin-bottom: 16px;
`;

const RememberMe = styled.label`
  display: flex;
  align-items: center;

  input {
    margin-right: 8px;
  }
`;

const RightContainer = styled.div`
  flex: 1;
  background-color: #4f46e5;
  display: flex;
  align-items: center;
  justify-content: center;
  padding: 24px;

  @media (max-width: 768px) {
    display: none;
  }
`;

const RightBackground = styled.div`
  width: 100%;
  height: 100%;
  background-image: url('https://source.unsplash.com/random');
  background-size: cover;
  background-position: center;
  border-radius: 12px;
`;

const SignInDiv = styled.div`
  width: 100%;
  padding: 12px;
  border-radius: 8px;
  background-color: #ea4335;
  text-align: center;
  cursor: pointer;
  margin-bottom: 12px;

  & > div {
    display: flex;
    justify-content: center;
    align-items: center;
    height: 100%;
    width: 100%;
    border-radius: 8px;
    background: transparent; /* Tentando tornar o fundo do botão transparente */
    border: none; /* Removendo borda do botão */
  }

  &:hover {
    background-color: #d93025;
  }
`;

export interface userI {
  name: string | null;
  iat?: number;
  iss?: string;
  picture?: string;
}

const Login: React.FC = () => {
  const [user, setUser] = useState<userI>({ name: null });

  function handleLoginSuccess(userObject: any) {
    setUser(userObject as userI);
    localStorage.setItem("token", JSON.stringify(userObject));
  }

  function handleCallbackResponse(response: any) {
    console.log("Encoded JWT ID token:" + response.credential);
    const userObject = jwtDecode(response.credential);
    console.log(userObject);

    setUser(userObject as userI);
    document.getElementById("signInDiv")!.hidden = true;
    localStorage.setItem("token", JSON.stringify(userObject));
  }

  function handleSignout() {
    setUser({ name: null });
    document.getElementById("signInDiv")!.hidden = false;
    localStorage.removeItem("token");
  }

  useEffect(() => {
    /* global google */
    google.accounts.id.initialize({
      client_id: "488131369581-oms526oiaqc55adh4bd8rusd3i8e8qoo.apps.googleusercontent.com",
      callback: handleCallbackResponse,
    });

    google.accounts.id.renderButton(
      document.getElementById("signInDiv")!,
      {
        size: "large",
        text: "signin_with", // Mostra "Fazer login com o Google"
        shape: "rectangular",
        logo_alignment: "center", // Centraliza o logo do Google
        width: "100%", // Alinha o botão ao tamanho do contêiner
        type: "standard"
      }
    );

    google.accounts.id.prompt();
  }, []);

  return (
    <Container>
      <LeftContainer>
        <FormContainer>
          <Title>Bem vindos à Eng Comp!</Title>
          <Input type="email" placeholder="Enter your email address" />
          <Input type="password" placeholder="Enter password" />
          <RememberMeContainer>
            <RememberMe>
              <input type="checkbox" />
              <span>Remember me</span>
            </RememberMe>
            <a href="/">Forgot your password?</a>
          </RememberMeContainer>
          <Button>Log in</Button>
          {Object.keys(user).length !== 0}
          {/* Usando o componente SignInDiv */}
          <SignInDiv id="signInDiv"></SignInDiv>
          <Button onClick={() => handleSignout()}>Sign out</Button>
          {user &&
            <div>
              <img src={user.picture} alt="user" />
              <h3>{user.name}</h3>
            </div>
          }
          <p>
            Don't have an account? <a href="/">Register here</a>
          </p>
        </FormContainer>
      </LeftContainer>
      <RightContainer>
        <RightBackground />
      </RightContainer>
    </Container>
  );
};

export default Login;
